{
DutyManager.LeaveActiveDuty();
if (await Coroutine.Wait(30000, () => CommonBehaviors.IsLoading))
{
await Coroutine.Yield();
await Coroutine.Wait(Timeout.Infinite, () => !CommonBehaviors.IsLoading);
await Coroutine.Sleep(1000);
}
}


