public static async Task<bool> LogAutosellLevel(int level)
{
	try 
	{
		string workingDirectory = Directory.GetCurrentDirectory();
		string settingsPath = Path.Combine(workingDirectory, "Settings");
		string logFilePath = Path.Combine(settingsPath, $"autosell{level}.txt");

		if (File.Exists(logFilePath))
		{
			string content = File.ReadAllText(logFilePath).Trim();
			if (content == "2")
			{
				return true;
			}
		}

		Directory.CreateDirectory(settingsPath);
		File.WriteAllText(logFilePath, "1");
		return true;
	}
	catch
	{
		return true;
	}
}


